import React, { useState } from 'react';

function Board() {
  //Hooks
  const [turn, setTurn] = useState(0);

  //Variables
  const [boardState, setBoard] = useState([' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ']);
  //Functions
  
  const move = (e) => {
    console.log("Called!");
    let mark = (turn === 1) ? 'X' : 'O';
    if(boardState[e.id] === ' ') {
      boardState[e.id] = mark;
    }
    setTurn(turn === 0 ? 1 : 0);
  }
  

  return (
    <div className="container-sm w-50">
        <div className="row">
          <div className="col cell text-center" id="0">
            <div className="input input-1" onClick={(e) => this.move(e)}>{boardState[0]}</div>
          </div>
          <div className="col cell text-center" id="1">
            <div className="input input-1" onClick={(e) => move(e)}>{boardState[1]}</div>
          </div>
          <div className="col cell text-center" id="2">
            <div className="input input-1" onClick={(e) => move(e)}>{boardState[2]}</div>
          </div>
        </div>
        <div className="row text-center">
          <div className="col cell text-center" id="3">
            <div className="input input-1" onClick={(e) => move(e)}>{boardState[3]}</div>
          </div>
          <div className="col cell text-center" id="4">
            <div className="input input-1"  onClick={(e) => move(e)}>{boardState[4]}</div>
          </div>
          <div className="col cell text-center" id="5">
            <div className="input input-1" onClick={(e) => move(e)}>{boardState[5]}</div>
          </div>
        </div>
        <div className="row">
          <div className="col cell text-center" id="6">
            <div className="input input-1" onClick={(e) => move(e)}>{boardState[6]}</div>
          </div>
          <div className="col cell text-center" id="7">
            <div className="input input-1" onClick={(e) => move(e)}>{boardState[7]}</div>
          </div>
          <div className="col cell text-center" id="8">
            <div className="input input-1" onClick={(e) => move(e)}>{boardState[8]}</div>
          </div>
        </div>
        
    </div>
  );
  
}

export default Board;
