import React, { useState } from 'react';

function Board() {
  //Hooks
  const [turn, setTurn] = useState(0);

  //Variables
  const [boardState, setBoard] = useState([' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ']);
  //Functions
  
  function move(e) {
    console.log("Called!");
    let mark = (turn === 1) ? 'X' : 'O';
    if(boardState[e.target.id] === ' ') {
      boardState[e.target.id] = mark;
    }
    e.target.innerText = mark;
    setTurn(turn === 0 ? 1 : 0);
  }
  

  return (
    <div className="container-sm w-50">
        <div className="row">
          <div className="col cell text-center" id="0">
            <div className="input input-1" onClick={move}>{turn}</div>
          </div>
          <div className="col cell text-center" id="1">
            <div className="input input-1" onClick={(e) => move(e)}></div>
          </div>
          <div className="col cell text-center" id="2">
            <div className="input input-1" onClick={(e) => move(e)}></div>
          </div>
        </div>
        <div className="row text-center">
          <div className="col cell text-center" id="3">
            <div className="input input-1" onClick={(e) => move(e)}></div>
          </div>
          <div className="col cell text-center" id="4">
            <div className="input input-1"  onClick={(e) => move(e)}></div>
          </div>
          <div className="col cell text-center" id="5">
            <div className="input input-1" onClick={(e) => move(e)}></div>
          </div>
        </div>
        <div className="row">
          <div className="col cell text-center" id="6">
            <div className="input input-1" onClick={(e) => move(e)}></div>
          </div>
          <div className="col cell text-center" id="7">
            <div className="input input-1" onClick={(e) => move(e)}></div>
          </div>
          <div className="col cell text-center" id="8">
            <div className="input input-1" onClick={(e) => move(e)}></div>
          </div>
        </div>
        
    </div>
  );
  
}

export default Board;
